const newSub = "utnalove@gmail.com";

const getTheSubscriberId = (newSubEmail) => {
  mailerLite
    .getSubscribers()
    .then((subList) => {
      subList.filter((singleSub) => {
        if (singleSub.email === newSubEmail) {
          return singleSub.id;
        }
      });
    })
