import React, { Component } from "react";
const MailerLite = require("mailerlite-api-v2-node").default;

const mailerLite = MailerLite("fcdfb0c2d2ceece7eeb88b3362f051bb");
const newSub = "utnalove@gmail.com";

const getTheSubscriberId = (newSubEmail) => {
  mailerLite.getSubscribers().then((subList) => {
    subList.filter((singleSub) => {
      if (singleSub.email === newSubEmail) {
        console.log(singleSub.id);
      }
    });
  })

  ;
};

getTheSubscriberId(newSub);

// mailerLite
//   .addSubscriber({email: newSub})
//   .catch((error) => {
//     console.log(error);
//   });

class Subscribe extends Component {
  render() {
    return (
      <section className="subscribe-area ptb-100">
        <div className="container">
          <div className="section-title">
            <h2>Dołącz społeczności Sergio S Dorje</h2>
            <div className="bar"></div>
            <p>
              Zapisz się do newslettera. Otrzymasz wartościowe informajce
              bezpośrednio od Autora
            </p>
          </div>

          <form className="newsletter-form">
            <input
              type="email"
              className="form-control"
              placeholder="Twój adre email"
              name="EMAIL"
              required={true}
            />
            <button className="btn btn-primary" type="submit">
              Subscribe
            </button>
          </form>
        </div>
      </section>
    );
  }
}

export default Subscribe;
