import React, { Component } from "react";
const MailerLite = require("mailerlite-api-v2-node").default;

const mailerLite = MailerLite("fcdfb0c2d2ceece7eeb88b3362f051bb");

// with Promises
mailerLite.getSubscribers().then((sub) => {
    // ...
    sub.filter((singleSub) => {
        if (singleSub.email === "Sergio") {
            console.log(singleSub);
        }
    })
  })


newSub = "utnalove@gmail.com";

